document.addEventListener("DOMContentLoaded",()=>{Array.from(document.getElementsByClassName("modal-toggle")).forEach(function(e){e.onclick=function(){e.nextElementSibling.classList.add("is-active")}}),Array.from(document.getElementsByClassName("modal-close")).forEach(e=>{e.onclick=function(){e.parentElement.classList.remove("is-active")}}),Array.from(document.getElementsByClassName("modal-background")).forEach(e=>{e.onclick=function(){e.parentElement.classList.remove("is-active")}})});